mapper.select:
SELECT section_id, team_id, section_name, position, color, created_at
FROM flowtask_section
WHERE section_id = #{sectionId}

mapper.update:
UPDATE flowtask_section
SET section_name = #{sectionName},
    color = #{color, jdbcType=VARCHAR}
WHERE section_id = #{sectionId}

UPDATE flowtask_section
SET position = #{position}
WHERE section_id = #{sectionId}
